    enum SaleType {
        NORMAL_SALE_JOT,
        NORMAL_SALE_SOT
    }

    event UpdateTGEImplAddress(SaleType indexed tgeType, address newImpl);
    event TokenGenerationEventCreated(address indexed tgeInstance);

