contracts/external/ERC1155PresetPauserUp.content:
// SPDX-License-Identifier: AGPL-3.0-only

pragma solidity 0.8.19;

import '@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import {OWNER_ROLE, PAUSER_ROLE} from '../libraries/DataTypes.sol';
/**
 * @dev {ERC1155} token, including a pauser role that allows to stop all token transfers
 * (including minting and burning).
 *
 * This contract uses {AccessControl} to lock permissioned functions using the
 * different roles - head to its documentation for details.
 *
 * Adapted from OZ's ERC1155PresetMinterPauserUpgradeable.sol: removed inheritance of
 * ERC1155BurnableUpgradeable; removed MINTER_ROLE; replaced DEFAULT_ADMIN_ROLE with OWNER_ROLE;
 * grants roles to owner param rather than `_msgSender()`; added `setURI()`, to give owner ability
 * to set the URI after initialization; added `isAdmin()` helper and `onlyAdmin` modifier.
 */
contract ERC1155PresetPauserUpgradeable is
    Initializable,
    ContextUpgradeable,
    AccessControlEnumerableUpgradeable,
    ERC1155PausableUpgradeable
{

    /**
     * @dev Grants `OWNER_ROLE` and `PAUSER_ROLE` to the account that
     * deploys the contract.
     */
    function __ERC1155PresetPauser_init(address owner, string memory uri) internal onlyInitializing {
        __Context_init_unchained();
        __ERC165_init_unchained();
        __AccessControl_init_unchained();
        __AccessControlEnumerable_init_unchained();
        __ERC1155_init_unchained(uri);
        __Pausable_init_unchained();
        __ERC1155Pausable_init_unchained();
        __ERC1155PresetPauser_init_unchained(owner);
    }

    function __ERC1155PresetPauser_init_unchained(address owner) internal onlyInitializing {
        _setupRole(OWNER_ROLE, owner);
        _setupRole(PAUSER_ROLE, owner);

        _setRoleAdmin(PAUSER_ROLE, OWNER_ROLE);
        _setRoleAdmin(OWNER_ROLE, OWNER_ROLE);
    }

    function setURI(string memory newuri) external onlyAdmin {
        /// @dev Because the `newuri` is not id-specific, we do not emit a URI event here. See the comment
        /// on `_setURI()`.
        _setURI(newuri);
    }

    /**
     * @dev Pauses all token transfers.
     *
     * See {ERC1155Pausable} and {Pausable-_pause}.
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function pause() public virtual {
        require(hasRole(PAUSER_ROLE, _msgSender()), 'ERC1155PresetPauser: must have pauser role to pause');
        _pause();
    }

    /**
     * @dev Unpauses all token transfers.
     *
     * See {ERC1155Pausable} and {Pausable-_unpause}.
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function unpause() public virtual {
        require(hasRole(PAUSER_ROLE, _msgSender()), 'ERC1155PresetPauser: must have pauser role to unpause');
        _unpause();
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControlEnumerableUpgradeable, ERC1155Upgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155PausableUpgradeable) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function isAdmin() public view returns (bool) {
        return hasRole(OWNER_ROLE, _msgSender());
    }

    modifier onlyAdmin() {
        require(isAdmin(), 'Must have admin role to perform this action');
        _;
    }

    uint256[50] private __gap;
}

contracts/interfaces/IUniqueIdentity.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only

pragma solidity 0.8.19;

import '@openzeppelin/contracts-upgradeable/token/ERC1155/IERC1155Upgradeable.sol';

interface IUniqueIdentity is IERC1155Upgradeable {
    /// @notice Mint a new UniqueIdentity token to the msgSender
    /// @param id The id representing the KYC type of the UniqueIdentity
    /// @param expiresAt The expiration time of the signature
    /// @param signature An EIP-191 signature of the corresponding mint params:
    ///                  account, id, expiresAt, address(this), nonces[account], block.chainid
    ///                  from an address with the SIGNER_ROLE.
    function mint(uint256 id, uint256 expiresAt, bytes calldata signature) external payable;

    /// @notice Mint a new UniqueIdentity token to the `recipient`
    /// @param recipient The recipient address to be minted to.
    /// @param id The id representing the KYC type of the UniqueIdentity
    /// @param expiresAt The expiration time of the signature
    /// @param signature An EIP-191 signature of the corresponding mintTo params:
    ///                  (account, recipient, id, expiresAt, address(this), nonces[account], block.chainid)
    ///                  from an address with the SIGNER_ROLE.
    function mintTo(address recipient, uint256 id, uint256 expiresAt, bytes calldata signature) external payable;

    /// @notice Burn a UniqueIdentity token of `id` from the `account`
    /// @param account The account which currently owns the UID
    /// @param id The id representing the KYC type of the UniqueIdentity
    /// @param expiresAt The expiration time of the signature
    /// @param signature An EIP-191 signature of the corresponding burn params:
    ///                  (account, id, expiresAt, address(this), nonces[account], block.chainid)
    ///                  from an address with the SIGNER_ROLE.
    function burn(address account, uint256 id, uint256 expiresAt, bytes calldata signature) external;

    /// @notice Burn a UniqueIdentity token of `id` from the `account` by using SUPPER_ADMIN wallet
    /// @param account The account which currently owns the UID
    /// @param id The id representing the KYC type of the UniqueIdentity
    function burnFrom(address account, uint256 id) external;

}

