// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.19;

import {UntangledBase} from '../base/UntangledBase.sol';
import {Configuration} from '../libraries/Configuration.sol';

/**
 * @title Registry
 * @notice This contract stores mappings of useful "protocol config state", giving a central place
 *  for all other contracts to access it. These config vars
 *  are enumerated in the `Configuration` library, and can only be changed by admins of the protocol.
 * @author Untangled Team
 */
contract Registry is UntangledBase {
    mapping(uint8 => address) public contractAddresses;
    mapping(address => bool) public whiteListToAddresses;
    mapping(address => bool) public whiteListFromAddresses;

    event AddressUpdated(address owner, uint8 index, address oldValue, address newValue);

    function initialize() public initializer {
        __UntangledBase__init(_msgSender());
    }

    function _setAddress(uint8 addressIndex, address newAddress) private {
        emit AddressUpdated(_msgSender(), addressIndex, contractAddresses[addressIndex], newAddress);
        contractAddresses[addressIndex] = newAddress;
    }

    function getAddress(uint8 index) public view returns (address) {
        return contractAddresses[index];
    }

    function isValidNoteTokenTransfer(address from, address to) external view returns (bool) {
        return whiteListFromAddresses[from] || whiteListToAddresses[to];
    }

    function setWhiteListAddresses(
        address[] calldata addresses,
        bool[] calldata boolFroms,
        bool[] calldata boolTos
    ) public onlyAdmin {
        uint256 length = addresses.length;
        require(length == boolFroms.length && length == boolTos.length, 'Invalid length');
        for (uint256 i; i < length; i++) {
            whiteListFromAddresses[addresses[i]] = boolFroms[i];
            whiteListToAddresses[addresses[i]] = boolTos[i];
        }
    }

    function setSecuritizationManager(address newAddress) public onlyAdmin whenNotPaused {
        _setAddress(uint8(Configuration.CONTRACT_TYPE.SECURITIZATION_MANAGER), newAddress);
    }

    function setSecuritizationPool(address newAddress) public onlyAdmin whenNotPaused {
        _setAddress(uint8(Configuration.CONTRACT_TYPE.SECURITIZATION_POOL), newAddress);
    }

    function setNoteTokenFactory(address newAddress) public onlyAdmin whenNotPaused {
        _setAddress(uint8(Configuration.CONTRACT_TYPE.NOTE_TOKEN_FACTORY), newAddress);
    }

    function setTokenGenerationEventFactory(address newAddress) public onlyAdmin whenNotPaused {
        _setAddress(uint8(Configuration.CONTRACT_TYPE.TOKEN_GENERATION_EVENT_FACTORY), newAddress);
    }

    function setLoanAssetToken(address newAddress) public onlyAdmin whenNotPaused {
        _setAddress(uint8(Configuration.CONTRACT_TYPE.LOAN_ASSET_TOKEN), newAddress);
    }

    function setSecuritizationPoolValueService(address newAddress) public onlyAdmin whenNotPaused {
        _setAddress(uint8(Configuration.CONTRACT_TYPE.SECURITIZATION_POOL_VALUE_SERVICE), newAddress);
    }

    function setLoanKernel(address newAddress) public onlyAdmin whenNotPaused {
        _setAddress(uint8(Configuration.CONTRACT_TYPE.LOAN_KERNEL), newAddress);
    }

    function setGo(address newAddress) public onlyAdmin whenNotPaused {
        _setAddress(uint8(Configuration.CONTRACT_TYPE.GO), newAddress);
    }

    function setNoteTokenVault(address newAddress) public onlyAdmin whenNotPaused {
        address oldNoteTokenVault = getAddress(uint8(Configuration.CONTRACT_TYPE.NOTE_TOKEN_VAULT));
        if (oldNoteTokenVault != address(0)) {
            whiteListFromAddresses[oldNoteTokenVault] = false;
            whiteListToAddresses[oldNoteTokenVault] = false;
        }

        _setAddress(uint8(Configuration.CONTRACT_TYPE.NOTE_TOKEN_VAULT), newAddress);

        whiteListFromAddresses[newAddress] = true;
        whiteListToAddresses[newAddress] = true;
    }
}

contracts/tokens/ERC721/LATValidator.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.19;

import {EIP712Upgradeable} from '@openzeppelin/contracts-upgradeable/utils/cryptography/EIP712Upgradeable.sol';
import {ECDSAUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol';
import {SignatureCheckerUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/cryptography/SignatureCheckerUpgradeable.sol';
import {ERC165CheckerUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/introspection/ERC165CheckerUpgradeable.sol';
import {IPool} from '../../interfaces/IPool.sol';
import {UntangledMath} from '../../libraries/UntangledMath.sol';
import {IERC5008} from '../../interfaces/IERC5008.sol';
import {DataTypes} from '../../libraries/DataTypes.sol';

abstract contract LATValidator is IERC5008, EIP712Upgradeable {
    using SignatureCheckerUpgradeable for address;
    using ECDSAUpgradeable for bytes32;
    using ERC165CheckerUpgradeable for address;

    bytes32 internal constant LAT_TYPEHASH =
        keccak256('LoanAssetToken(uint256[] tokenIds,uint256[] nonces,address validator)');

    mapping(uint256 => uint256) internal _nonces;

    modifier validateCreditor(address creditor, DataTypes.LoanAssetInfo calldata info) {
        if (IPool(creditor).validatorRequired()) {
            _checkNonceValid(info);

            require(_checkValidator(info), 'LATValidator: invalid validator signature');
            require(isValidator(creditor, info.validator), 'LATValidator: invalid validator');
        }
        _;
    }

    modifier requireValidator(DataTypes.LoanAssetInfo calldata info) {
        require(_checkValidator(info), 'LATValidator: invalid validator signature');
        _;
    }

    modifier requireNonceValid(DataTypes.LoanAssetInfo calldata info) {
        _checkNonceValid(info);
        _;
    }

    function _checkNonceValid(DataTypes.LoanAssetInfo calldata info) internal {
        for (uint256 i = 0; i < info.tokenIds.length; i = UntangledMath.uncheckedInc(i)) {
            require(_nonces[info.tokenIds[i]] == info.nonces[i], 'LATValidator: invalid nonce');
            unchecked {
                _nonces[info.tokenIds[i]] = _nonces[info.tokenIds[i]] + 1;
            }

            emit NonceChanged(info.tokenIds[i], _nonces[info.tokenIds[i]]);
        }
    }

    function __LATValidator_init() internal onlyInitializing {
        __EIP712_init_unchained('UntangledLoanAssetToken', '0.0.1');
        __LATValidator_init_unchained();
    }

    function __LATValidator_init_unchained() internal onlyInitializing {}

    function isValidator(address pool, address sender) public view virtual returns (bool);

    function nonce(uint256 tokenId) external view override returns (uint256) {
        return _nonces[tokenId];
    }

    function _checkValidator(DataTypes.LoanAssetInfo calldata latInfo) internal view returns (bool) {
        bytes32 digest = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    LAT_TYPEHASH,
                    keccak256(abi.encodePacked(latInfo.tokenIds)),
                    keccak256(abi.encodePacked(latInfo.nonces)),
                    latInfo.validator
                )
            )
        );

        return latInfo.validator.isValidSignatureNow(digest, latInfo.validateSignature);
    }

    function domainSeparatorV4() external view returns (bytes32) {
        return _domainSeparatorV4();
    }

    uint256[50] private __gap;
}

contracts/tokens/ERC721/LoanAssetToken.s.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.19;

import {UntangledERC721} from './UntangledERC721.sol';
import {ConfigHelper} from '../../libraries/ConfigHelper.sol';
import {LATValidator} from './LATValidator.sol';
import {Registry} from '../../storage/Registry.sol';
import {DataTypes, VALIDATOR_ROLE} from '../../libraries/DataTypes.sol';
import {UntangledMath} from '../../libraries/UntangledMath.sol';
import {IAccessControlUpgradeable} from '@openzeppelin/contracts-upgradeable/access/IAccessControlUpgradeable.sol';

/**
 * LoanAssetToken: The representative for ownership of a Loan
 */
contract LoanAssetToken is UntangledERC721, LATValidator {
    using ConfigHelper for Registry;

    /** CONSTRUCTOR */
    function initialize(
        Registry _registry,
        string memory name,
        string memory symbol,
        string memory baseTokenURI
    ) public initializer {
        __UntangledERC721__init(name, symbol, baseTokenURI);
        __LATValidator_init();

        registry = _registry;

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());

        require(
            address(registry.getSecuritizationManager()) != address(0x0),
            'SECURITIZATION_MANAGER is zero address.'
        );

        require(address(registry.getLoanKernel()) != address(0x0), 'LOAN_KERNEL is zero address.');

        _setupRole(MINTER_ROLE, address(registry.getLoanKernel()));
        _revokeRole(MINTER_ROLE, _msgSender());
    }

    function safeMint(
        address creditor,
        DataTypes.LoanAssetInfo calldata latInfo
    ) public onlyRole(MINTER_ROLE) validateCreditor(creditor, latInfo) {
        for (uint i = 0; i < latInfo.tokenIds.length; i = UntangledMath.uncheckedInc(i)) {
            _safeMint(creditor, latInfo.tokenIds[i]);
        }
    }

    function isValidator(address pool, address sender) public view virtual override returns (bool) {
        return IAccessControlUpgradeable(pool).hasRole(VALIDATOR_ROLE, sender);
    }
}

contracts/tokens/ERC721/UntangledERC721..content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.19;

import '@openzeppelin/contracts-upgradeable/token/ERC721/presets/ERC721PresetMinterPauserAutoIdUpgradeable.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import '../../storage/Registry.sol';

abstract contract UntangledERC721 is ERC721PresetMinterPauserAutoIdUpgradeable {
    Registry public registry;

    string private _baseTokenURI;

    function __UntangledERC721__init(
        string memory name,
        string memory symbol,
        string memory baseTokenURI
    ) internal onlyInitializing {
        __UntangledERC721__init_unchained(name, symbol, baseTokenURI);
    }

    function __UntangledERC721__init_unchained(
        string memory name,
        string memory symbol,
        string memory baseTokenURI
    ) internal onlyInitializing {
        _baseTokenURI = baseTokenURI;
        __ERC721PresetMinterPauserAutoId_init(name, symbol, baseTokenURI);
    }

    function mint(address to, uint256 tokenId) public virtual onlyRole(MINTER_ROLE) {
        _mint(to, tokenId);
    }

    function safeMint(address to, uint256 tokenId) public virtual onlyRole(MINTER_ROLE) {
        _safeMint(to, tokenId);
    }

    function setBaseURI(string memory baseTokenURI) public virtual onlyRole(DEFAULT_ADMIN_ROLE) {
        _baseTokenURI = baseTokenURI;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        _requireMinted(tokenId);

        string memory baseURI = _baseURI();
        return
            bytes(baseURI).length > 0
                ? string(
                    abi.encodePacked(
                        baseURI,
                        Strings.toHexString(tokenId),
                        '?chain_id=',
                        Strings.toString(block.chainid)
                    )
                )
                : '';
    }

    uint256[48] private __gap;
}
